mapper.update:
update member 
set
	member_login_pwd = #{memberLoginPwd},
	member_email = #{memberEmail},
	member_address = #{memberAddress}
where member_id = #{memberId}

mapper.select:
select member_login_id, member_login_pwd
from member
where member_login_id = #{memberLoginId}
and member_login_pwd = #{memberLoginPwd}
